const popup=document.querySelector("#popup"),resetBtn=document.querySelector(".resetbtn"),displayCount=document.querySelector("#count"),count=localStorage.getItem("counter")||0;let counter=parseInt(count);const handleOpenPopup=()=>{const e=document.querySelector(".btn-btn");e.addEventListener("click",()=>{popup.classList.add("show"),counter++,localStorage.setItem("counter",counter),5<(displayCount.innerHTML=counter)&&(resetBtn.style.display="block")})},handleClosePopup=()=>{const e=document.querySelector(".btn-close");e.addEventListener("click",()=>{popup.classList.remove("show")})},handleClosePopuOut=()=>{const t=document.querySelector(".popup__container");document.addEventListener("click",e=>{e.preventDefault(),e.target==t&&popup.classList.remove("show")})},handleResetCounter=()=>{resetBtn.addEventListener("click",()=>{counter=0,localStorage.setItem("counter",counter),resetBtn.style.display="none",popup.classList.remove("show")})};handleOpenPopup(),handleClosePopup(),handleClosePopuOut(),handleResetCounter();